svg.script:
function animateText() {
  const text = "Gen";
  const genText = document.getElementById('genText');
  const cursor = document.getElementById('cursor');
  let index = 0;
  let isDeleting = false;

  function updateText() {
    if (!isDeleting && index <= text.length) {
      genText.textContent = text.substring(0, index);
      cursor.setAttribute('x', 80 + index * 14);
      index++;
      if (index > text.length) {
        isDeleting = true;
        setTimeout(updateText, 1000); // Wait before starting to delete
      } else {
        setTimeout(updateText, 200);
      }
    } else if (isDeleting && index >= 0) {
      genText.textContent = text.substring(0, index);
      cursor.setAttribute('x', 80 + index * 14);
      index--;
      if (index < 0) {
        isDeleting = false;
        setTimeout(updateText, 500); // Wait before restarting
      } else {
        setTimeout(updateText, 100);
      }
    }
  }

  updateText();
}

animateText();
